import { useEffect, useState } from 'react';
import './App.css';
import SingleCard from './components/SingleCard';
import Modal from './components/Modal';
const cardImages = [
  {"src": "/img/helmet-1.png", matched: false},
  {
    "src": "/img/potion-1.png", matched: false
  },
  {
    "src": "/img/ring-1.png", matched: false
  },
  {
    "src": "/img/scroll-1.png", matched: false
  },
  {
    "src": "/img/shield-1.png", matched: false
  },
  {
    "src": "/img/sword-1.png", matched: false
  }
]

function App() {

  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)
  const[score, setScore] = useState(0)
  const[choiceOne, setChoiceOne] = useState(null)
  const[choiceTwo, setChoiceTwo] = useState(null)
  const[disabled, setDisabled] = useState(false)
  const[timeLeft, setTimeLeft] = useState(30);
  const [showModal, setShowModal] = useState(false);

  // shuffle cards
  const shuffleCards = ( ) => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(()=> Math.random() - 0.5)
      .map((card)=>({...card, id: Math.random() }))
    setChoiceOne(null)
    setChoiceTwo(null)
    setCards(shuffledCards)
    setTurns(0)
    setTimeLeft(30)
    setScore(0)
    setShowModal(false)
  }

  //handle a choice
  const handleChoice = (card) =>{
    choiceOne ? setChoiceTwo(card) : setChoiceOne(card)

  }
  //select 2 cards to check for match 
  useEffect (() => {

    if(choiceOne && choiceTwo){
      setDisabled(true)
      if(choiceOne.src === choiceTwo.src){
        setCards(prevCards => {
          return prevCards.map(card => {
            if(card.src === choiceOne.src){
              return {
               ...card,
                matched: true
              }
            }else{
              return card
            }
          })
        })
        setScore(prevScore => prevScore + 100);
        resetTurn()
      }else{
        setTimeout(() => resetTurn(), 1000)
      }

    }
  }, [choiceOne, choiceTwo])

  console.log(cards)

  //timer for the game
  useEffect(() => {
    let timerID;
    if(timeLeft > 0 && cards.length && !cards.every(card => card.matched)){
      timerID = setTimeout(()=> setTimeLeft(time-1), 1000);

    } else if(timeLeft === 0 || cards.every(card => card.matched)){
      setShowModal(true);
    }
    return () => {
      clearTimeout(timerID);
    };
  },[timeLeft, cards])

  // reset the game

    const resetTurn = () => {
      setChoiceOne(null)
      setChoiceTwo(null)
      setTurns(prevTurns => prevTurns + 1)
      setDisabled(false)
    }

    useEffect(() => {
      shuffleCards()
    }, [])


  return (
    <div className="App">

     <h1>Magic Match</h1>
     <p>Score: {score}</p> {/*Display the Score */}
     <button onClick ={shuffleCards}>New Game</button>
     <div className="card-grid">
        {cards.map(card => (
          <SingleCard
           key={card.id} 
           card = {card}
           handleChoice = {handleChoice}
           flipped = {card === choiceOne || card === choiceTwo || card.matched}
           disabled={disabled}
           />
        ))}
     </div>
     <p>Turns: {turns}</p>
     <Modal>
        <h2>{cards.every(card => card.matched) ? 'You are Victorius!': 'You have been defeated'}</h2>
        <button onClick ={shuffleCards}> back to the practice cages </button>
     </Modal>
    </div>
  );
}

export default App;
